package main

import (
	"log"
	"os"

	"github.com/m4xw311/musing/cmd/musings/cmd"
)

func main() {
	if err := cmd.Execute(); err != nil {
		log.Println(err)
		os.Exit(1)
	}
}

package cmd

import (
	"github.com/spf13/cobra"
)

var rootCmd = &cobra.Command{
	Use:   "musings",
	Short: "A tool to publish markdown-based static blogs",
	Long: `Musings is a simple tool to publish markdown based static blog 
and publish the same to platforms like substack and medium.`,
}

func Execute() error {
	return rootCmd.Execute()
}

func init() {
	rootCmd.AddCommand(publishCmd)
	rootCmd.AddCommand(syncCmd)
}

package cmd

import (
	"fmt"

	"github.com/m4xw311/musing/internal/blog"
	"github.com/m4xw311/musing/internal/site"
	"github.com/spf13/cobra"
)

var publishCmd = &cobra.Command{
	Use:   "publish",
	Short: "Publish blog posts to static website",
	Long:  `Publish blog posts written in markdown to a static website.`,
	Run: func(cmd *cobra.Command, args []string) {
		fmt.Println("Publishing blog posts...")

		// Create blog instance
		b := blog.NewBlog("posts")

		// Load existing posts
		if err := b.LoadPosts(); err != nil {
			fmt.Printf("Error loading posts: %v\n", err)
			return
		}

		// Create static site generator
		s := site.NewStaticSiteGenerator("posts", "public")

		// Generate site
		if err := s.Generate(); err != nil {
			fmt.Printf("Error generating site: %v\n", err)
			return
		}

		fmt.Println("Blog posts published successfully to public/ directory!")
	},
}

package cmd

import (
	"fmt"

	"github.com/m4xw311/musing/internal/blog"
	"github.com/spf13/cobra"
)

var syncCmd = &cobra.Command{
	Use:   "sync",
	Short: "Sync blog posts to external platforms",
	Long:  `Sync blog posts to platforms like Substack and Medium.`,
	Run: func(cmd *cobra.Command, args []string) {
		fmt.Println("Syncing blog posts to external platforms...")

		// Create blog instance
		b := blog.NewBlog("posts")

		// Load existing posts
		if err := b.LoadPosts(); err != nil {
			fmt.Printf("Error loading posts: %v\n", err)
			return
		}

		fmt.Println("Blog posts synced successfully!")
	},
}

package blog

import (
	"bufio"
	"fmt"
	"html/template"
	"os"
	"path/filepath"
	"sort"
	"strings"
	"time"

	"github.com/gomarkdown/markdown"
)

// Post represents a blog post
type Post struct {
	Title              string        // Extracted from the first # Heading
	Content            string        // Extracted from the markdown file
	ContentHTML        template.HTML // HTML version of the content
	ContentSnippet     string        // Snippet of the content for cards
	ContentSnippetHTML template.HTML // HTML version of the content snippet
	CreatedDate        time.Time     // Parsed from frontmatter
	UpdatedDate        time.Time     // Parsed from frontmatter
	Slug               string        // Derived from title
	Tags               []string
	Published          bool
}

// Blog represents a collection of blog posts
type Blog struct {
	Posts []Post
	Path  string
}

// NewBlog creates a new blog instance
func NewBlog(path string) *Blog {
	return &Blog{
		Posts: make([]Post, 0),
		Path:  path,
	}
}

// LoadPosts loads all blog posts from the blog directory
func (b *Blog) LoadPosts() error {
	// Create directory if it doesn't exist
	if err := os.MkdirAll(b.Path, 0755); err != nil {
		return err
	}

	// Clear existing posts
	b.Posts = make([]Post, 0)

	// Read all markdown files in the directory
	err := filepath.Walk(b.Path, func(path string, info os.FileInfo, err error) error {
		if err != nil {
			return err
		}

		if !info.IsDir() && filepath.Ext(path) == ".md" {
			post, err := parsePost(path)
			if err != nil {
				return fmt.Errorf("error parsing post %s: %w", path, err)
			}
			b.Posts = append(b.Posts, post)
			fmt.Printf("Loaded post: %s\n", post.Title)
		}

		return nil
	})

	if err != nil {
		return err
	}

	// Sort posts by CreatedDate in descending order (newest first)
	sort.Slice(b.Posts, func(i, j int) bool {
		return b.Posts[i].CreatedDate.After(b.Posts[j].CreatedDate)
	})

	return nil
}

// parsePost parses a markdown file into a Post struct
func parsePost(filePath string) (Post, error) {
	post := Post{}

	_, err := os.Stat(filePath)
	if err != nil {
		return post, err
	}

	file, err := os.Open(filePath)
	if err != nil {
		return post, err
	}
	defer file.Close()

	scanner := bufio.NewScanner(file)
	frontmatter := make(map[string]string)
	contentLines := make([]string, 0)
	var rawFrontmatterLines []string

	// Parse frontmatter - files start with frontmatter, then "---", then content
	inFrontmatter := false
	frontmatterProcessed := false

	// Read first line to check if it starts with ---
	if scanner.Scan() {
		firstLine := scanner.Text()
		if firstLine == "---" {
			inFrontmatter = true
			rawFrontmatterLines = append(rawFrontmatterLines, firstLine)
		} else {
			// No frontmatter, just content
			contentLines = append(contentLines, firstLine)
		}
	}

	// Continue processing the rest of the file
	for scanner.Scan() {
		line := scanner.Text()

		// Check for frontmatter separator
		if line == "---" && inFrontmatter && !frontmatterProcessed {
			// End of frontmatter, start of content
			inFrontmatter = false
			frontmatterProcessed = true
			rawFrontmatterLines = append(rawFrontmatterLines, line)
			continue
		}

		// Process frontmatter or content
		if inFrontmatter {
			// Collect raw frontmatter lines
			rawFrontmatterLines = append(rawFrontmatterLines, line)
			// Parse frontmatter key: value
			parts := strings.SplitN(line, ":", 2)
			if len(parts) == 2 {
				key := strings.TrimSpace(parts[0])
				value := strings.TrimSpace(parts[1])
				frontmatter[key] = value
			}
		} else {
			// Process content
			contentLines = append(contentLines, line)
		}
	}

	if err := scanner.Err(); err != nil {
		return post, err
	}

	// Always use current time for missing dates to avoid any file timestamp issues
	defaultTime := time.Now()

	updatedFile := false

	// Not doing this to simplify the work of the user
	// if title, ok := frontmatter["Title"]; ok {
	// 	post.Title = title
	// }

	// Extract title from first # heading in content
	post.Title = extractTitleFromContent(contentLines)

	if createdStr, ok := frontmatter["CreatedDate"]; ok {
		if created, err := time.Parse("2006-01-02 15:04:05", createdStr); err == nil {
			post.CreatedDate = created
		} else {
			fmt.Fprintf(os.Stderr, "Invalid CreatedDate in %s. Must be in format '2006-01-02 15:04:05'\n", filePath)
		}
	} else {
		// If no CreatedDate, use current time
		post.CreatedDate = defaultTime
		updatedFile = true
		fmt.Printf("Missing CreatedDate in %s, setting to current time\n", filePath)
	}

	if updatedStr, ok := frontmatter["UpdatedDate"]; ok {
		if updated, err := time.Parse("2006-01-02 15:04:05", updatedStr); err == nil {
			post.UpdatedDate = updated
		} else {
			fmt.Fprintf(os.Stderr, "Invalid UpdatedDate in %s. Must be in format '2006-01-02 15:04:05'\n", filePath)
		}
	} else {
		// If no UpdatedDate, use current time
		post.UpdatedDate = post.CreatedDate
		updatedFile = true
		fmt.Printf("Missing UpdatedDate in %s, setting same as CreatedDate\n", filePath)
	}

	// Update the file ONLY if we added missing date fields
	// Do NOT update if both dates already existed and were valid
	if updatedFile {
		if err := updatePostFile(filePath, rawFrontmatterLines, frontmatter, contentLines, post); err != nil {
			fmt.Printf("Warning: Could not update %s with missing date fields: %v\n", filePath, err)
		}
	}

	if tagsStr, ok := frontmatter["Tags"]; ok {
		tags := strings.Split(tagsStr, ",")
		for i, tag := range tags {
			tags[i] = strings.TrimSpace(tag)
		}
		post.Tags = tags
	}

	if publishedStr, ok := frontmatter["Published"]; ok {
		post.Published = strings.ToLower(publishedStr) == "true"
	}

	// Derive slug from title
	post.Slug = createSlug(post.Title)

	// Set content
	post.Content = strings.Join(contentLines, "\n")
	// Convert markdown content to HTML
	htmlContent := markdown.ToHTML([]byte(post.Content), nil, nil)
	post.ContentHTML = template.HTML(htmlContent)

	// Generate content snippet
	post.ContentSnippet = generateContentSnippet(post.Content)
	// Convert markdown snippet to HTML
	htmlContentSnippet := markdown.ToHTML([]byte(post.ContentSnippet), nil, nil)
	post.ContentSnippetHTML = template.HTML(htmlContentSnippet)
	return post, nil
}

// updatePostFile updates the markdown file with missing date fields
func updatePostFile(filePath string, rawFrontmatterLines []string, frontmatter map[string]string, contentLines []string, post Post) error {
	// Create the updated content
	var updatedContent strings.Builder

	// Check if we have frontmatter
	hasFrontmatter := len(rawFrontmatterLines) > 0
	hasValidFrontmatterStructure := hasFrontmatter && len(rawFrontmatterLines) >= 2 &&
		rawFrontmatterLines[0] == "---" && rawFrontmatterLines[len(rawFrontmatterLines)-1] == "---"

	if hasFrontmatter && hasValidFrontmatterStructure {
		// Check which date fields are missing
		hasCreatedDate := false
		hasUpdatedDate := false

		// Check existing frontmatter for date fields
		for key := range frontmatter {
			if key == "CreatedDate" {
				hasCreatedDate = true
			}
			if key == "UpdatedDate" {
				hasUpdatedDate = true
			}
		}

		// Write first frontmatter separator
		updatedContent.WriteString("---\n")

		// Write existing frontmatter lines (excluding separators)
		for i := 1; i < len(rawFrontmatterLines)-1; i++ {
			updatedContent.WriteString(rawFrontmatterLines[i] + "\n")
		}

		// Add missing date fields if needed
		if !hasCreatedDate {
			updatedContent.WriteString(fmt.Sprintf("CreatedDate: %s\n", post.CreatedDate.Format("2006-01-02 15:04:05")))
		}

		if !hasUpdatedDate {
			updatedContent.WriteString(fmt.Sprintf("UpdatedDate: %s\n", post.UpdatedDate.Format("2006-01-02 15:04:05")))
		}

		// Write closing frontmatter separator
		updatedContent.WriteString("---\n")
	} else if hasFrontmatter {
		// Frontmatter exists but missing proper separators
		// Check which date fields are missing
		hasCreatedDate := false
		hasUpdatedDate := false

		// Check existing frontmatter for date fields
		for key := range frontmatter {
			if key == "CreatedDate" {
				hasCreatedDate = true
			}
			if key == "UpdatedDate" {
				hasUpdatedDate = true
			}
		}

		// Write first frontmatter separator
		updatedContent.WriteString("---\n")

		// Write existing frontmatter lines
		for _, line := range rawFrontmatterLines {
			// Skip any accidental separators in the content
			if line != "---" {
				updatedContent.WriteString(line + "\n")
			}
		}

		// Add missing date fields if needed
		if !hasCreatedDate {
			updatedContent.WriteString(fmt.Sprintf("CreatedDate: %s\n", post.CreatedDate.Format("2006-01-02 15:04:05")))
		}

		if !hasUpdatedDate {
			updatedContent.WriteString(fmt.Sprintf("UpdatedDate: %s\n", post.UpdatedDate.Format("2006-01-02 15:04:05")))
		}

		// Write closing frontmatter separator
		updatedContent.WriteString("---\n")
	} else {
		// No existing frontmatter, create new one
		updatedContent.WriteString("---\n")
		updatedContent.WriteString(fmt.Sprintf("CreatedDate: %s\n", post.CreatedDate.Format("2006-01-02 15:04:05")))
		updatedContent.WriteString(fmt.Sprintf("UpdatedDate: %s\n", post.UpdatedDate.Format("2006-01-02 15:04:05")))
		updatedContent.WriteString("---\n")
	}

	// Write content
	for _, line := range contentLines {
		updatedContent.WriteString(line + "\n")
	}

	// Write the updated content back to the file
	return os.WriteFile(filePath, []byte(updatedContent.String()), 0644)
}

// extractTitleFromContent extracts the first # heading from content lines
func extractTitleFromContent(lines []string) string {
	for _, line := range lines {
		if strings.HasPrefix(line, "# ") {
			return strings.TrimSpace(strings.TrimPrefix(line, "# "))
		}
	}
	return "Untitled"
}

// createSlug creates a URL-friendly slug from a title
func createSlug(title string) string {
	// Convert to lowercase
	slug := strings.ToLower(title)
	// Replace spaces and special characters with hyphens
	slug = strings.Join(strings.Fields(slug), "-")
	// Remove any remaining non-alphanumeric characters (except hyphens)
	slug = strings.Map(func(r rune) rune {
		if r >= 'a' && r <= 'z' || r >= '0' && r <= '9' || r == '-' {
			return r
		}
		return -1
	}, slug)
	// Remove leading/trailing hyphens
	slug = strings.Trim(slug, "-")
	return slug
}

// generateContentSnippet generates a short snippet from the content
func generateContentSnippet(content string) string {
	// Remove markdown headers and code blocks
	//	re := regexp.MustCompile("(?m)^(#.*|```.*|```)

	// Limit to first 150 characters
	if len(content) > 150 {
		content = content[:150] + "..."
	}

	return content
}

package site

import (
	"fmt"
	"html/template"
	"io"
	"os"
	"path/filepath"

	"github.com/m4xw311/musing/internal/blog"
)

// StaticSiteGenerator handles generating static HTML from markdown posts
type StaticSiteGenerator struct {
	PostsDir  string
	OutputDir string
}

// NewStaticSiteGenerator creates a new static site generator
func NewStaticSiteGenerator(postsDir, outputDir string) *StaticSiteGenerator {
	return &StaticSiteGenerator{
		PostsDir:  postsDir,
		OutputDir: outputDir,
	}
}

// IndexData holds the data for the index page
type IndexData struct {
	Posts       []blog.Post
	LatestPosts []blog.Post
}

// Generate generates the static site
func (s *StaticSiteGenerator) Generate() error {
	// Create output directory
	if err := os.MkdirAll(s.OutputDir, 0755); err != nil {
		return err
	}

	// Load blog posts
	b := blog.NewBlog(s.PostsDir)
	if err := b.LoadPosts(); err != nil {
		return fmt.Errorf("error loading posts: %w", err)
	}

	// Copy style.css to the output directory
	if err := s.copyStyleCSS(); err != nil {
		return fmt.Errorf("error copying style.css: %w", err)
	}

	// Copy images directory to the output directory
	if err := s.copyImages(); err != nil {
		return fmt.Errorf("error copying images: %w", err)
	}

	// Generate index page
	if err := s.generateIndex(b); err != nil {
		return fmt.Errorf("error generating index: %w", err)
	}

	// Generate individual post pages
	if err := s.generatePosts(b); err != nil {
		return fmt.Errorf("error generating posts: %w", err)
	}

	// Generate RSS feed
	if err := s.generateRSSFeed(b); err != nil {
		return fmt.Errorf("error generating RSS feed: %w", err)
	}

	// Generate Atom feed
	if err := s.generateAtomFeed(b); err != nil {
		return fmt.Errorf("error generating Atom feed: %w", err)
	}

	return nil
}

// copyStyleCSS copies the style.css file to the output directory
func (s *StaticSiteGenerator) copyStyleCSS() error {
	src := "internal/template/style.css"
	dst := filepath.Join(s.OutputDir, "style.css")

	srcFile, err := os.Open(src)
	if err != nil {
		return err
	}
	defer srcFile.Close()

	dstFile, err := os.Create(dst)
	if err != nil {
		return err
	}
	defer dstFile.Close()

	_, err = io.Copy(dstFile, srcFile)
	return err
}

// copyImages copies the images directory from posts to the output directory
func (s *StaticSiteGenerator) copyImages() error {
	src := filepath.Join(s.PostsDir, "images")
	dst := filepath.Join(s.OutputDir, "images")

	// Check if source directory exists
	if _, err := os.Stat(src); os.IsNotExist(err) {
		// No images directory, nothing to copy
		return nil
	} else if err != nil {
		return err
	}

	// Create destination directory
	if err := os.MkdirAll(dst, 0755); err != nil {
		return err
	}

	// Copy all files in the images directory
	return filepath.Walk(src, func(path string, info os.FileInfo, err error) error {
		if err != nil {
			return err
		}

		// Skip directories
		if info.IsDir() {
			return nil
		}

		// Calculate relative path
		relPath, err := filepath.Rel(src, path)
		if err != nil {
			return err
		}

		// Create destination file path
		dstPath := filepath.Join(dst, relPath)

		// Create destination directory if it doesn't exist
		dstDir := filepath.Dir(dstPath)
		if err := os.MkdirAll(dstDir, 0755); err != nil {
			return err
		}

		// Copy file
		return s.copyFile(path, dstPath)
	})
}

// copyFile copies a file from src to dst
func (s *StaticSiteGenerator) copyFile(src, dst string) error {
	srcFile, err := os.Open(src)
	if err != nil {
		return err
	}
	defer srcFile.Close()

	dstFile, err := os.Create(dst)
	if err != nil {
		return err
	}
	defer dstFile.Close()

	_, err = io.Copy(dstFile, srcFile)
	return err
}

// generateIndex creates the index page with a list of all posts
func (s *StaticSiteGenerator) generateIndex(b *blog.Blog) error {
	// Prepare data for the index page
	var latestPosts []blog.Post
	if len(b.Posts) > 4 {
		latestPosts = b.Posts[:4]
	} else {
		latestPosts = b.Posts
	}

	indexData := IndexData{
		Posts:       b.Posts,
		LatestPosts: latestPosts,
	}

	tmpl, err := template.ParseFiles("internal/template/index.html")
	if err != nil {
		return err
	}

	file, err := os.Create(filepath.Join(s.OutputDir, "index.html"))
	if err != nil {
		return err
	}
	defer file.Close()

	return tmpl.Execute(file, indexData)
}

// generatePosts creates individual HTML pages for each post
func (s *StaticSiteGenerator) generatePosts(b *blog.Blog) error {
	tmpl, err := template.ParseFiles("internal/template/post.html")
	if err != nil {
		return err
	}

	for _, post := range b.Posts {
		file, err := os.Create(filepath.Join(s.OutputDir, post.Slug+".html"))
		if err != nil {
			return err
		}

		err = tmpl.Execute(file, post)
		file.Close()
		if err != nil {
			return err
		}
	}

	return nil
}

body {
    font-family: Arial, sans-serif;
    margin: 0;
    padding: 0;
    background-color: #f4f4f4;
    position: relative;
    min-height: 100vh;
}

header {
    background-color: #333;
    color: #fff;
    padding: 10px 0;
    text-align: center;
    position: relative;
}

header h1 {
    margin: 0;
    color: #fff; /* Ensure the header text is white */
}

header h1 a {
    color: #fff; /* Ensure the header link text is white */
    text-decoration: none;
}

.feed-link {
    position: absolute;
    top: 50%;
    right: 20px;
    transform: translateY(-50%);
}

.feed-link a {
    display: inline-block;
    background: rgba(255, 255, 255, 0.1);
    padding: 8px;
    border-radius: 4px;
    transition: background 0.3s ease;
}

.feed-link a:hover {
    background: rgba(255, 255, 255, 0.2);
}

main {
    padding: 20px;
    max-width: 800px;
    margin: 20px auto;
    background-color: #fff;
    border-radius: 5px;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
    box-sizing: border-box; /* Include padding and border in element's total width and height */
    padding-bottom: 60px; /* Add space to prevent overlap with footer */
}

.cards {
    display: block;
    margin-bottom: 20px;
}

.card {
    width: 100%;
    padding: 15px;
    border: 1px solid #ddd;
    border-radius: 5px;
    background-color: #f9f9f9;
    transition: transform 0.3s ease, box-shadow 0.3s ease;
    margin-bottom: 20px; /* Add space between cards */
    box-sizing: border-box; /* Include padding and border in element's total width and height */
}

.card h3 {
    margin-top: 0;
}

.card a {
    text-decoration: none;
    color: #333;
}

/* Remove the hover underline for card links */
.cards a {
    text-decoration: none;
}

.cards a:hover {
    text-decoration: none;
}

/* Add shadow and float effect on hover */
.cards a:hover .card {
    transform: translateY(-5px);
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.2);
}

/* But we'll keep the hover effect for regular links in the article */
article {
    margin-bottom: 20px;
}

h1, h2 {
    color: #333;
}

ul {
    list-style-type: none;
    padding: 0;
}

li {
    margin-bottom: 10px;
}

a {
    color: #333;
    text-decoration: none;
}

a:hover {
    text-decoration: underline;
}

footer {
    text-align: center;
    padding: 10px 0;
    background-color: #333;
    color: #fff;
    position: fixed;
    width: 100%;
    bottom: 0;
}

/* Ensure images scale to fit within the content area */
main img {
    max-width: 100%;
    height: auto;
}

/* Ensure images are never taller than screen height and are center aligned */
img {
    max-height: 80vh;
    width: auto;
    object-fit: contain;
    display: block;
    margin: 0 auto;
}

package site

import (
	"encoding/xml"
	"fmt"
	"os"
	"path/filepath"
	"time"

	"github.com/m4xw311/musing/internal/blog"
)

// RSSFeed represents an RSS feed
type RSSFeed struct {
	XMLName xml.Name    `xml:"rss"`
	Version string      `xml:"version,attr"`
	Channel *RSSChannel `xml:"channel"`
}

// RSSChannel represents an RSS channel
type RSSChannel struct {
	Title       string    `xml:"title"`
	Link        string    `xml:"link"`
	Description string    `xml:"description"`
	Language    string    `xml:"language,omitempty"`
	PubDate     string    `xml:"pubDate,omitempty"`
	Items       []RSSItem `xml:"item"`
}

// RSSItem represents an RSS item
type RSSItem struct {
	Title       string `xml:"title"`
	Link        string `xml:"link"`
	Description string `xml:"description"`
	PubDate     string `xml:"pubDate"`
	GUID        string `xml:"guid"`
}

// AtomFeed represents an Atom feed
type AtomFeed struct {
	XMLName  xml.Name    `xml:"feed"`
	Xmlns    string      `xml:"xmlns,attr"`
	Title    string      `xml:"title"`
	Subtitle string      `xml:"subtitle,omitempty"`
	ID       string      `xml:"id"`
	Updated  string      `xml:"updated"`
	Author   *AtomAuthor `xml:"author,omitempty"`
	Entries  []AtomEntry `xml:"entry"`
}

// AtomAuthor represents an Atom author
type AtomAuthor struct {
	Name string `xml:"name"`
}

// AtomEntry represents an Atom entry
type AtomEntry struct {
	Title   string      `xml:"title"`
	ID      string      `xml:"id"`
	Link    AtomLink    `xml:"link"`
	Updated string      `xml:"updated"`
	Summary string      `xml:"summary"`
	Content AtomContent `xml:"content"`
}

// AtomLink represents an Atom link
type AtomLink struct {
	Href string `xml:"href,attr"`
	Rel  string `xml:"rel,attr,omitempty"`
	Type string `xml:"type,attr,omitempty"`
}

// AtomContent represents Atom content
type AtomContent struct {
	Type string `xml:"type,attr"`
	Body string `xml:",chardata"`
}

// generateRSSFeed creates an RSS feed from blog posts
func (s *StaticSiteGenerator) generateRSSFeed(b *blog.Blog) error {
	channel := &RSSChannel{
		Title:       "My Blog",               // This should be configurable
		Link:        "http://localhost:8080", // This should be configurable
		Description: "A blog about technology and programming",
		Language:    "en-us",
	}

	// Add items for each post
	for _, post := range b.Posts {
		if !post.Published {
			continue
		}

		pubDate := post.CreatedDate.Format(time.RFC1123Z)
		if channel.PubDate == "" {
			channel.PubDate = pubDate
		}

		item := RSSItem{
			Title:       post.Title,
			Link:        fmt.Sprintf("http://localhost:8080/%s.html", post.Slug), // This should be configurable
			Description: string(post.ContentSnippetHTML),
			PubDate:     pubDate,
			GUID:        fmt.Sprintf("http://localhost:8080/%s.html", post.Slug), // This should be configurable
		}

		channel.Items = append(channel.Items, item)
	}

	rss := &RSSFeed{
		Version: "2.0",
		Channel: channel,
	}

	output, err := xml.MarshalIndent(rss, "", "  ")
	if err != nil {
		return err
	}

	filePath := filepath.Join(s.OutputDir, "rss.xml")
	file, err := os.Create(filePath)
	if err != nil {
		return err
	}
	defer file.Close()

	// Add XML header
	_, err = file.WriteString(xml.Header)
	if err != nil {
		return err
	}

	_, err = file.Write(output)
	if err != nil {
		return err
	}

	fmt.Printf("Generated RSS feed: %s\n", filePath)
	return nil
}

// generateAtomFeed creates an Atom feed from blog posts
func (s *StaticSiteGenerator) generateAtomFeed(b *blog.Blog) error {
	feed := &AtomFeed{
		Xmlns:    "http://www.w3.org/2005/Atom",
		Title:    "My Blog", // This should be configurable
		Subtitle: "A blog about technology and programming",
		ID:       "http://localhost:8080", // This should be configurable
	}

	// Add entries for each post
	for _, post := range b.Posts {
		if !post.Published {
			continue
		}

		updated := post.UpdatedDate.Format(time.RFC3339)
		if feed.Updated == "" {
			feed.Updated = updated
		}

		entry := AtomEntry{
			Title:   post.Title,
			ID:      fmt.Sprintf("http://localhost:8080/%s.html", post.Slug),                 // This should be configurable
			Link:    AtomLink{Href: fmt.Sprintf("http://localhost:8080/%s.html", post.Slug)}, // This should be configurable
			Updated: updated,
			Summary: string(post.ContentSnippetHTML),
			Content: AtomContent{
				Type: "html",
				Body: string(post.ContentHTML),
			},
		}

		feed.Entries = append(feed.Entries, entry)
	}

	output, err := xml.MarshalIndent(feed, "", "  ")
	if err != nil {
		return err
	}

	filePath := filepath.Join(s.OutputDir, "atom.xml")
	file, err := os.Create(filePath)
	if err != nil {
		return err
	}
	defer file.Close()

	// Add XML header
	_, err = file.WriteString(xml.Header)
	if err != nil {
		return err
	}

	_, err = file.Write(output)
	if err != nil {
		return err
	}

	fmt.Printf("Generated Atom feed: %s\n", filePath)
	return nil
}
